execution.script:
import groovy.transform.Field
import java.io.*;
import java.text.SimpleDateFormat;

//Variables Nexus
@Field defaultRepository = 'jenkins-releases'
@Field defaultExtension = 'zip'
@Field groupId = 'oracle-DB'
@Field credentialsNexus = 'nexusAdmin'
@Field ipNexus = 'http://192.168.1.110:8081'
//Variables git
@Field creditialsGit = 'ugithub'
@Field urlRepo = 'https://github.com/hidraxos/jenkins_check.git'
//Variables logica
@Field labelParameters = []
@Field scriptName = ""
@Field listVersiones=[]
@Field BANK = 'mine'
@Field listaLogs = []
@Field credentialsOracle
@Field def date = new Date()

@Field def yellow = "43"; @Field def green = "42"; @Field def red = '41'; @Field def blue = '46'


node('master'){

    labelParameters.add([$class: 'BooleanParameterDefinition', defaultValue: false, description: 'Opcional', name: 'Refresh_Parameters'])
    labelParameters.add([$class: 'BooleanParameterDefinition', defaultValue: false, description: 'Script de backup', name: 'BACKUP'])
    labelParameters.add([$class: 'BooleanParameterDefinition', defaultValue: false, description: 'Script de Ejecucion', name: 'EXECUTE'])
    labelParameters.add([$class: 'BooleanParameterDefinition', defaultValue: false, description: 'Script de Reverso', name: 'REVERSE'])
    labelParameters.add([$class: 'BooleanParameterDefinition', defaultValue: false, description: '多Validar logs despues de todas las ejecuciones?', name: 'validarLogsPostEjecuciones'])
	labelParameters.add(string(defaultValue: '', description: 'COMMIT GIT ${urlRepo}', name: 'COMMIT', trim: false))		
	labelParameters.add(string(defaultValue: '', description: '	多script especifico o inicio de secuencia?', name: 'INDEX', trim: false))
    labelParameters.add([$class: 'BooleanParameterDefinition', defaultValue: false, description: '', name: 'SECUENCIAL'])
    properties(
        [parameters(
            labelParameters
        )]
    )
    stage('Git'){
        if(params.'Refresh_Parameters'==false){
            cleanWs()
            if(params.'BACKUP' == false && params.'EXECUTE' == false && params.'REVERSE' == false){
		    	error("[ERROR] No Se selecciono componente de ejecucion.")
			}
			if(params.'COMMIT' == ""){
				error("[ERROR] No Se ingreso COMMIT de FILE GITHUB.")
			}
            checkout([$class: 'GitSCM', branches: [[name: '*/master']], extensions: [], userRemoteConfigs: [[url: 'https://github.com/hidraxos/jenkins_check.git']]])
            def lines = readFile('sql_prueba.txt').readLines()
            if(params.'INDEX' == ""){
                for(VERSIONES in lines){
                    listVersiones.add(VERSIONES)
                }
            }else if(params.'INDEX'!="" && params.'SECUENCIAL'==false){
                for(version in lines){
                    def (valor1, valor2, valor3) = VERSIONES.tokenize( '#' )
                    if(valor1 == params.'INDEX'){
                        listVersiones.add(VERSIONES)
                    }
                }
            }else if(params.'INDEX' != "" && params.'SECUENCIAL' == true){
				def contador = 1
				for(String VERSIONES in lines){
					def (valor1, valor2, valor3) = VERSIONES.tokenize( '#' )
					if(params.'INDEX' == valor1){
						contador = 2
					}
					if(contador == 2){
						listaVersiones.add(VERSIONES)
					}
				}
            }else{
                printm("No se encontro archivo de versiones en GitHub",red)
                error("File Not Found")
            }
        }else{
            printm("Refresh Parameters....",blue)
        }
    }
    stage('nexus'){
        if(params.'Refresh_Parameters'==false){
            for(version in listVersiones){
                def (valor1, valor2, valor3) = version.tokenize( '#' )
                repoFin(valor3)
                descarga(valor1 ,artifactId)
            }
        }else{
            printm("Refresh Parameters....",blue)
        }
    }
    stage('Descomprimido'){
        if(params.'Refresh_Parameters'==false){
            for(version in listVersiones){
                descomprimido(version)
            }
        }else{
            printm("Refresh Parameters....",blue)
        }
    }
    stage('Validacione Scripts'){
        if(params.'Refresh_Parameters'==false){
            for(def version in listaVersiones){
                def (valor1, valor2, valor3) = version.tokenize( '#' )
			    defineEntorno(valor2)
                if(valor2 == "TX"){
					scriptExecute = 'migrate.sql'
					scriptExecuteH = 'reverse.sql'
                    scriptExecuteB = 'backup.sql'
                }else if(valor2 == "HIST"){
					scriptExecute = 'migrate.sql'
					scriptExecuteH = 'reverse.sql'
                    scriptExecuteB = 'backup.sql'
				}
                if(BACKUP == "true"){
					finallScript = '${valor1}/migration/${scriptExecute}'
					CRITICAlANALYSYS(finallScript)
				}
				if(EXECUTE == "true"){
					finallScript = '${valor1}/migration/${scriptExecuteB}'
					CRITICAlANALYSYS(finallScript)
				}
				if(REVERSE == "true"){
					finallScript = '${valor1}/migration/${scriptExecuteH}'
					CRITICAlANALYSYS(finallScript)
				}
            }
        }else{
            printm("Refresh Parameters....",blue)
        }
    }
    stage('Ejecutar'){
        if(params.'Refresh_Parameters'==false){
            def folder
            def script
            for(version in listVersiones){
                def (valor1, valor2, valor3) = version.tokenize( '#' )
			    defineEntorno(valor2)
                if(valor2 == "TX"){
					scriptExecute = 'migrate.sql'
					scriptExecuteH = 'reverse.sql'
                    scriptExecuteB = 'backup.sql'
                }else if(valor2 == "HIST"){
					scriptExecute = 'migrate.sql'
					scriptExecuteH = 'reverse.sql'
                    scriptExecuteB = 'backup.sql'
				}
                if(params.'BACKUP'){
                    folder = "${version}/migration/${version}"
                    script = scriptExecuteB
                    if(fileExists(folder+'/'+script)){
                        SQL(folder,script)
                        if(params.'validarLogsPostEjecuciones' == true){
							listaLogs.add(folder+'/'+scriptName)
						}
                    }else{
                        printm ("No se encuentra script de backup",yellow)
                    }
                }
                if(params.'EXECUTE'){
                    folder = "${version}/migration/${version}"
                    script = scriptExecute
                    SQL(folder,script)
                    if(params.'validarLogsPostEjecuciones' == true){
						listaLogs.add(folder+'/'+scriptName)
					}
                }
                if(params.'REVERSE'){
                    folder = "${version}/reverse"
                    script = scriptExecuteH
                    SQL(folder,script)
                    if(params.'validarLogsPostEjecuciones' == true){
						listaLogs.add(folder+'/'+scriptName)
					}
                }
            }
            if(validarLogsPostEjecuciones == "true"){
				for(log in listaLogs){
					AnalisisLogs(log)
				}
			}
        }else{
            printm("Refresh Parameters....",blue)
        }
    }   
}
def descarga(version,artifactId){
    printm ("Descarga de .tar.gz Nexus",blue)
    withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
        sh"curl -X GET -u ${USERNAME}:${PASS} ${ipNexus}/repository/${defaultRepository}/${groupId}/${artifactId}/${version}/sql-releases-${version}.zip -O"
    }   
    printm ("Descarga a Nexus EXITOSA",green)
}
def printm(message, color){
    ansiColor('xterm'){
	    def	sdf = new SimpleDateFormat("M/d/yyyy HH:mm:ss")
        println "\033[${color}m************ ${message} ************ ${sdf.format(date)}\033[0m"
    }
}
def descomprimido(version){
    sh "tar -xzvf sql-releases-${version}.tar.gz"
}
def SQL(folder, scriptName){
	printm('[INFO] Realizando ejecucion de: '+folder+'/'+scriptName+'','46m')
	withCredentials([usernamePassword(credentialsId: credentialsOracle, usernameVariable: 'USERNAME', passwordVariable: 'PASSWORD')]) {

		sh """cd ${folder} && sqlplus64 '${USERNAME}/${PASSWORD}@(DESCRIPTION=(ADDRESS=(PROTOCOL=TCP)(Host=${hostDB})(Port=${portDB}))(CONNECT_DATA=(SID=${sidDB})))' @${scriptName}
		"""
	}						
}
def defEntorno(entorno){
    if(entorno =="TX"){
        hostDB = '192.168.1.110'
        portDB = '51521'
        sidDB = 'XE'
        artifactId = 'sql-releases'
        credentialsOracle = 'oracleDB'
    }else{
        printm("No selecciono entorno",red)
        error("Enviroment Not Selected")
    }

}
def CRITICAlANALYSYS(script){
    printm('[INFO] Ejecutandose analisis script: ${script}',blue)
    def DICC=["DROP"]
    def file = readFile(script)
    for(palabra in DICC){
        if(file.contains(palabra)){
            printm("[ERROR] Se encontro la palabra ${palabra} en el script ${script}",red)
            error("${palabra} action not allowed")
        }else{
            printm("No se enocontraron palabras reservadas",green)
        }
    }
}
def AnalisisLogs(scriptName){
	printm('[INFO] Ejecutando analisis de log generado:'+scriptName+'',blue)
	def (exp_version) = scriptName.substring(13).tokenize( '/' )
	def ERRORES = ["ORA-","SP2-"]
	fileResult = scriptName+'.log'
	def readFileResult = readFile(fileResult).trim()
	for(def palabra in ERRORES){
		if(readFileResult.contains(palabra)){
			sh "tar -rvf error.log.tar ${scriptName}.log"
			NOTIFICATION("error.log.tar","FAILED", exp_version)
			error("[ERROR] Genero error el script: ${scriptName}")
		}	
	}
	sh "tar -rvf LOGS-DB-B"+BANK+"-"+exp_version+".tar ${scriptName}.log"
	NOTIFICATION("LOGS-DB-B"+BANK+"-"+exp_version+".tar","SUCCESS", exp_version)
}
def NOTIFICATION(logName, resultado, VERSION){
	def BODY
	if(resultado == "FAILED"){
		BODY = '''${SCRIPT, template="groovy-html.template"}'''
	}else{

		BODY = '''${SCRIPT, template="groovy-html.template"} Logs de Ejecucion: https://devops-nexus.ath.net/service/rest/repository/browse/'''+defaultRepository+'''/LOGS-DB/B'''+BANK+'''-PRD/'''+VERSION+'''-${BUILD_NUMBER}/'''
	}
    emailext attachLog: true, attachmentsPattern: "${logName}", body:BODY, subject: "${JOB_NAME} # ${BUILD_NUMBER} # ${resultado} B"+BANK+"-PRD", to: "hidraxosjenkins@gmail.com"	
}
