execution.script:
import groovy.transform.Field

//------Variables GitHub ----
@Field def credentialsGit = 'ugithub'
@Field def urlRepo = "https://github.com/hidraxos/jenkins_check.git"

//------Variables server properties----
@Field def host = '192.168.1.115'
@Field def credentials = 'hidraxos_credentials'
@Field def folder = '/home/hidraxos/Documents/properties'

//------Variables Nexus---------
@Field def url_nexus = 'http://192.168.1.115:8081'
@Field def repoNexus = 'maven-icbs-releases'
@Field def credentialsNexus = 'nexus_credentials'
@Field def artifactIdProperties = "icbs-release-json"
@Field def groupId = "com/ath/icbs"

//------Variables logicas
@Field def yellow = "43"; @Field def green = "42"; @Field def red = '41'; @Field def blue = '46' ; @Field def white = "47"
@Field def data

pipeline{
    agent any

    parameters { 
        booleanParam(name: 'Refresh_Param', defaultValue: false, description: '')
        string(name: 'Release', defaultValue: '', description: 'Release que trae el properties.json') 
    }

    stages{
        stage('Download json') {
            steps {
                script{
                    if(!params."Refresh_Param"){
                        downloadNexus(artifactIdProperties)
                        data = readJSON file: "properties.json"
                    }else{
                        printm('Refrescando parametros',blue)
                    }
                }
            }
        }
        stage('Mod Properties'){
            steps {
                script{
                    if(!params."Refresh_Param"){
                        for (properties in data.Properties){
                            println properties
                            if(properties.bancos.contains("BAVV")){
                                if(properties.accion_archivo == "Mod"){
                                    actionLine(properties)
                                }else if(bancos.accion_archivo == "ADD"){
                                    actionFile(properties)
                                }
                            }
                        }
                    }else{
                        printm("Refrescando parametros",blue)
                    }
                }
            }
        }
    }
}
def printm(message,color){
    ansiColor{
        println("\033[${color}m*************** ${message} ***************\033[0m")
    }
}
def actionFile(info){
    if(info.accion_archivo == "Add"){
        printm("Se anadira el archivo ${info.nombre_archivo}",white)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} scp ${info.nombre_archivo} ${USERNAME}@${host}:${folder}
            """
            printm("Archivo ${info.nombre_archivo} agregado",blue)
            sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "ls ${folder}"
            """
        }
    }else if(info.accion_archivo == "Elim"){
        printm("Se Eliminara el archivo ${info.nombre_archivo}",white)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "rm -rf ${info.nombre_archivo}"
            """
            printm("Archivo ${info.nombre_archivo} agregado",blue)
            sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "ls ${folder}"
            """
        }
    }
}
def actionLine(info){
    for(modificacion in info.modificacion){
        if(modificacion.accion_linea == "New"){
            printm("Se anadira linea archivo ${info.nombre_archivo}",white)
            withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
                sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "echo '${modificacion.propiedad}=${modificacion.valor}' >> ${folder}/${info.nombre_archivo}"
                """
                printm("Archivo ${info.nombre_archivo} modificado",blue)
                sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${info.nombre_archivo}"
                """
            }
        }else if(modificacion.accion_linea == "Elim"){
            printm("Se eliminara una linea ${info.nombre_archivo}",white)
            withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
                sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "sed -i -e '/${info.Line}/d' ${folder}/${info.nombre_archivo}"
                """
                printm("Archivo ${info.nombre_archivo} modificado",blue)
                sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${info.nombre_archivo}"
                """
            }
        }else if(modificacion.accion_linea == "Mod"){
            printm("Se modificara una linea ${info.nombre_archivo}",white)
            withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
                sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "sed -i -e 's/${info.OldLine}/${info.NewLine}/' ${folder}/${info.nombre_archivo}"
                """
                printm("Archivo ${info.nombre_archivo} modificado",blue)
                sh"""
                    sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${info.nombre_archivo}"
                """
            }
        }
    }
}
def downloadNexus(def artifact){
    withCredentials([usernamePassword(credentialsId: credentialsNexus, passwordVariable: 'PASS', usernameVariable: 'USER')]) {
        sh"curl -u $USER:$PASS -X GET ${url_nexus}/repository/${maven-icbs-releases}/${groupId}/${artifact}/${version}/${version}.json -O"
    }
}
