execution.script:
import groovy.transform.Field

@Field def host = '192.168.1.115'
@Field def credentials = 'hidraxos_credentials'
@Field def credentialsGit = 'ugithub'
@Field def yellow = "43"; @Field def green = "42"; @Field def red = '41'; @Field def blue = '46' ; @Field def white = "47"
@Field def folder = '/home/hidraxos/Documents'
@Field def urlRepo = "https://github.com/hidraxos/jenkins_check.git"

pipeline{
    agent any

    parameters { 
        string(name: 'COMMIT', defaultValue: '', description: 'Commit del properties.yaml') 
    }

    stages{
        stage('Download yml') {
            steps {
                script{
                    checkout([$class: 'GitSCM', branches: [[name: COMMIT ]], doGenerateSubmoduleConfigurations: false, extensions: [], submoduleCfg: [], userRemoteConfigs: [[credentialsId: credentialsGit, url: urlRepo]]])
                    data = readJSON file: "properties.json"
                    println data.Properties
                    for(def info in data.BAVV){
                        actionFile(info)
                    }
                }
            }
        }
    }
}
def printm(message,color){
    ansiColor{
        println("\033[${color}m*************** ${message} ***************\033[0m")
    }
}
def actionFile(info){
    if(info.ActionFile == "Add"){
        printm("Se anadira el archivo ${info.name}",white)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} scp ${info.name} ${USERNAME}@${host}:${folder}
            """
            printm("Archivo ${info.name} agregado",blue)
            sh"""
                     sshpass -p ${PASS} ssh ${USERNAME}@${host} "ls ${folder}"
            """
        }
    }else if(info.ActionFile == "Mod"){
        actionLine(info)
    }
}
def actionLine(info){
    if(info.ActionLine == "Add"){
        printm("Se anadira linea archivo ${info.name}",white)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "echo '${info.NewLine}' >> ${folder}/${info.name}"
            """
            printm("Archivo ${info.name} modificado",blue)
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${info.name}"
            """
        }
    }else if(info.ActionLine == "Elim"){
        printm("Se eliminara una linea ${info.name}",white)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "sed -i -e '/${info.Line}/d' ${folder}/${info.name}"
            """
            printm("Archivo ${info.name} modificado",blue)
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${info.name}"
            """
        }
    }else if(info.ActionLine == "Mod"){
        printm("Se modificara una linea ${info.name}",white)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "sed -i -e 's/${info.OldLine}/${info.NewLine}/' ${folder}/${info.name}"
            """
            printm("Archivo ${info.name} modificado",blue)
            sh"""
                sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${info.name}"
            """
        }
    }

}
