execution.script:
import groovy.transform.Field
import java.io.*;

@Field def host = '192.168.1.115'
@Field def credentials = 'hidraxos_credentials'
@Field def credentialsGit = 'ugithub'
@Field def yellow = "43"; @Field def green = "42"; @Field def red = '41'; @Field def blue = '46'
@Field def folder = '/home/hidraxos/Documents'

pipeline{
    agent any
    parameters{
        booleanParam(name: 'Refresh_Param', defaultValue: false, description: '')
        booleanParam(name: 'Modificar', defaultValue: false, description: 'Seleccione si desea modificar una linea')
        text(name: 'Modificar_text', defaultValue: '', description: 'Ingrese file##line##line')
        booleanParam(name: 'Eliminar', defaultValue: false, description: 'Seleccione si desea Eliminar una linea')
        text(name: 'Eliminar_text', defaultValue: '', description: 'Ingrese file##line')
        booleanParam(name: 'Agregar', defaultValue: false, description: 'Seleccione si desea agregar una linea')
        text(name: 'Agregar_text', defaultValue: '', description: 'Ingrese file##line')
        booleanParam(name: 'Archivo_Nuevo', defaultValue: false, description: '')
        string(name:'Archivo',description: "nombre del archivo .properties nuevo")
    }
    stages{
        stage('Git properties'){
            steps{
                script{
                    cleanWs()
                    if(!params.'Refresh_Param'){
                        checkout([$class: 'GitSCM', branches: [[name: '*/master']], extensions: [], userRemoteConfigs: [[credentialsId: 'ugithub', url: 'https://github.com/hidraxos/jenkins_check.git']]])
                        printm("Descarga de Git exitosa",green)
                        printm("Ejecutando cambios en propiedades",blue)
                    }else{
                        printm("Refrescando Parametros",blue)
                    }
                }
            }
        }
        stage('Execute changes'){
            steps{
                script{
                    if(!params.'Refresh_Param'){
                        if(params.'Modificar'){
                            modify()
                        }
                        if(params.'Eliminar'){
                            eliminate()
                        }
                        if(params.'Agregar'){
                            agregate()
                        }
                        if(params.'Archivo_Nuevo'){
                            if(fileExists(params.'Archivo'.trim())){
                                new_file()
                            }else{
                                printm("No existe el archivo ${params.'Archivo'}",red)
                                error("file Doesn't exist")
                            }
                        }
                    }
                }
            }
        }
    }
}
def printm(message,color){
    ansiColor{
        println("\033[${color}m############ ${message} ############\033[0m")
    }
}

def modify(){
    array_files = params.'Modificar_text'.tokenize(';;')
    for(i in array_files){
        array = i.tokenize('##')
        file = array[0] ; old = array[1] ; new_line = array[2]
        printm("Modificando archivo ${file}",blue)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "sed -i -e 's/${old}/${new_line}/' ${folder}/${file}"
            """
            printm("Archivo ${file} modificado",green)
            sh"""
                sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${file}"
            """
        }
    }
}
def eliminate(){
    array_files = params.'Eliminar_text'.tokenize(';;')
    for(i in array_files){
        array = i.tokenize('##')
        file = array[0] ; line = array[1] ;
        printm("Eliminando linea de archivo ${file}",blue)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "sed -i -e '/${line}/d' ${folder}/${file}"
            """
            printm("Archivo ${file} modificado",green)
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${file}"
            """
        }
    }
}
def agregate(){
    array_files = params.'Agregar_text'.tokenize(';;')
    for(i in array_files){
        array = i.tokenize('##')
        file = array[0] ; line = array[1]
        printm("Agregando linea al archivo ${file}",blue)
        withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "echo '${line}' >> ${folder}/${file}"
            """
            printm("Archivo ${file} modificado",green)
            sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "cat ${folder}/${file}"
            """
        }
    }
}
def new_file(){
    printm("Agregando archivo Nuevo ${params.'Archivo'}",blue)
    withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
        file = params.'Archivo'.trim()
        sh"""
             sshpass -p ${PASS} scp ${file} ${USERNAME}@${host}:${folder}
        """
        printm("Archivo ${params.'Archivo'} agregado",green)
        sh"""
                 sshpass -p ${PASS} ssh ${USERNAME}@${host} "ls ${folder}"
        """
   }
}
