import groovy.transform.Field
import groovy.json.JsonSlurper
import java.io.IOException;
import java.util.Arrays;
import java.util.ArrayList;
import java.util.Collections;
import java.util.Iterator;
import java.util.List;
import java.io.*;
import org.w3c.dom.*;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.ParserConfigurationException;
import groovy.json.JsonSlurper
import java.text.SimpleDateFormat;

@Field def ruta = ""
@Field def existe
@Field def rango
@Field def credentials = 'nexusAdmin'
@Field def pathBackup = '/home/jenkins/'      //Ruta donde se generaran las carpetas para el respaldo
@Field def pathJenkins = '/var/jenkins_home/jobs/'  //Ruta donde se guardan los archivos de configuracion de jenkins
@Field def url_github = 'https://github.com/hidraxos/jobs_vencidos'
@Field def url_nexus = 'http://192.168.1.110:8081'
@Field def repoNexus = "Jobs_vencidos"
@Field def date
@Field def yellow = "43"
@Field def green = "42"

node{
    cleanWs()
    properties([parameters([
        booleanParam(defaultValue: false, description: 'Seleccione si desea ELIMINAR LOS JOBS DESPUES DEL RESPALDO',name: "ELIMINAR_JOBS"),
        booleanParam(defaultValue: false, description: '',name: "Refresh_Param"),
        stringParam(defaultValue: "", description: 'Nombre del archivo en GitHub SIN EL .txt',name: "Archivo"),
        stringParam(defaultValue: "", description: 'Version del .tar para cargar a Nexus',name: "Version")
    ])])
    stage('check-folder-respaldos'){
        if(!params."Refresh_Param"){
            if(!validate("")){
                sh"""
                    mkdir ${pathBackup}${params."Archivo"}
                """
            }else{
                println "El directorio ${pathBackup}${params."Archivo"} ya existe"
            }
        }
    }
    stage('Back_up_jobs'){
        if(!params."Refresh_Param"){
            checkout([$class: 'GitSCM', branches: [[name: '*/master']], extensions: [], userRemoteConfigs: [[url: url_github]]])
            def archivoJobs = (params."Archivo")+".txt"
            def jobs = readFile(archivoJobs).readLines();
            if(!jobs.isEmpty()){
                printm("Comenzando Respaldo de Jobs",yellow)
                respaldos(jobs)
                printm("Respaldo de Jobs EXITOSO",green)
                printm("Comenzando comprimido .tar.gz",yellow)
                comprimido()
                printm("Comprimido EXITOSO",green)
                uploadNexus()
            }
        }
    }
    stage('Eliminar Jobs'){
        if(!params."Refresh_Param"){
            if(params."ELIMINAR_JOBS"){
                printm("Comienzo de Borrado de Jobs",yellow)
                def jobs = (params."Archivo")+".txt"
                def eliminateJobs = readFile(jobs).readLines()
                eliminate(eliminateJobs)
                printm("Borrado de Jobs EXITOSO",green)
                printm("El Borrado se completara al reiniciar Jenkins",yellow)
            }
        }
    }
}
def respaldos(def jobs){
    for(i in jobs){
        //println i
        assert (!i.contains(" "))
        if(i.contains('/')){
            def arreglo = i.tokenize('/')
            //println arreglo


            rango = []
            for(h in 0..(arreglo.size()-1)){
                rango.add(h)
            }


            for(e in rango){
                ruta = ruta + "/" +arreglo[e]
                //println ruta
                    if(!validate(ruta)){
                        sh"""
                            mkdir ${pathBackup}${params."Archivo"}${ruta}
                        """
                    }
                    if(!validate(ruta+"/config.xml")){
                        sh """
                          cp  ${pathJenkins}${ruta}/config.xml ${pathBackup}${params."Archivo"}${ruta}
                        """
                    }
                    if(e != (arreglo.size()-1)){
                        ruta = ruta + "/jobs"
                        if(!validate(ruta)){
                            sh"""
                                mkdir ${pathBackup}${params."Archivo"}${ruta}
                            """
                        }
                    }
            }
            ruta = ""
        }else{
            if(!validate("/"+i)){
                sh"""
                    mkdir ${pathBackup}${params."Archivo"}"/"${i}
                """
            }
            if(!validate("/"+i+"/config.xml")){
                sh """
                  cp  ${pathJenkins}/${i}/config.xml ${pathBackup}${params."Archivo"}"/"${i}
                """
            }
        }
    }
}

def boolean validate(def ruta){
    if(fileExists("${pathBackup}${params."Archivo"}"+ruta)){
        println "${ruta} existe"
        return true
    }else{
        println "${ruta} NO existe"
        return false 
    }
}

def comprimido(){
    sh"""
        tar -czvf job_vencidos.tar.gz ${pathBackup}${params."Archivo"}
    """
}

def uploadNexus(){
    println "\033[43m############  Carga de .tar.gz a Nexus ############\033[0m"
    def date = new Date()
    def sdf = new SimpleDateFormat("dd-MM-yyyy")
    date = sdf.format(date)
    withCredentials([usernamePassword(credentialsId: credentials, passwordVariable: 'PASS', usernameVariable:'USERNAME')]){
        sh"curl -v -u $USERNAME:$PASS -X POST ${url_nexus}/service/rest/v1/components?repository=${repoNexus} -F maven2.groupId=BackUp_Jobs_Vencidos -F maven2.artifactId=${date} -F maven2.version=${params."Version"} -F 'maven2.asset1=@${WORKSPACE}/job_vencidos.tar.gz;type=application/x-tgz' -F maven2.asset1.extension=tar.gz"
    }   
    println "\033[42m############  Carga a Nexus EXITOSA ############\033[0m"
}

def eliminate(def eliminateJobs){
    for(i in eliminateJobs){
        if(i.contains('/')){
            def arreglo = i.tokenize('/')
            def path = ""
            for(e in 0..(arreglo.size()-2)){
                path = path + arreglo[e]+"/jobs/"
            }
            def ruta = path
            path = path + "/"+arreglo[(arreglo.size()-1)]
            sh"rm -rf ${pathJenkins}${path}"
            eliminateIfEmpty(ruta)
            path = ""
        }else{
            sh "rm -rf ${pathJenkins}${i}"
        }
    }
}

def eliminateIfEmpty(path){
    path = pathJenkins + path                     
    def arreglo = path.tokenize("/")              
    arreglo.remove(arreglo.size()-1)              
    def ruta_sin_jobs = ""
    for(i in arreglo){
        ruta_sin_jobs = ruta_sin_jobs + "/" + i  
    }
    def  File carpeta = new File(path)             
    def files = carpeta.list();
    if(files.length == 0){
        sh"rm -rf ${ruta_sin_jobs}"
    }else{
        println "La carpeta aun tiene Jobs en uso"
    }
}
def printm(message,color){
    ansiColor{
        println("\033[${color}m############ ${message} ############\033[0m")
    }
}
