definition.script:
import groovy.transform.Field
import java.text.SimpleDateFormat;

@Field def folders=["jobs","nodes","plugins","users"]
@Field def yellow = "43"; @Field def green = "42"; @Field def red = '41'; @Field def blue = '46'
@Field def date = new Date()

pipeline {
    agent{
        label 'ubuntu'
    }
    triggers{ cron('H H 1,15 1-11 *') }
    stages{
        stage('respaldo') {
            steps{
                script{
                    printm("Se respaldaran las carpetas JOBS NODES PLUGINS USER",blue)
                    dir('/home/jenkins/respaldos'){
                        sh"rm -rf *"
                        for(folder in folders){
                            sh"""
                                docker cp hidraxos:/var/jenkins_home/${folder}/ ./${folder}/
                            """
                        }
                        sh"""
                            git add .
                            git commit -am 'respaldo ${date}'
                            git push -u origin master
                        """
                    }
                }
            }
        }
    }
}


def printm(message, color){

	def	sdf = new SimpleDateFormat("M/d/yyyy HH:mm:ss")
    println "\033[${color}m************ ${message} ************ ${sdf.format(date)}\033[0m"
}
